pragma solidity >=0.4.21 <0.7.0;

contract escrow{

//The employer, employee and payment amount
string public _category;
address public _contractor;
address public _worker;
uint public _payAMount;
function setCate () public {
  _category = "SchoolWOrk";
}

// boolean that checks when the job has been accepted
bool public initiated = false;

//boolean that checks if the job has been completed.
bool public finalized = false;

//An integer that measures the value of the escrow funds
uint public escrowfunds;

//An integer that denotes the site comission fee based on the Job Payment Payment AMount
uint public _comish;

function JobEscrow(
     address,
     address,
     uint
  ) public {
    // require _seller and _buyer to be non-zero, ie legitamite addresses, and unique.
    require(_contractor != address(0));
    require(_worker != address(0));
    require(_worker != _contractor);

    // require the contract creator to be either _buyer or _seller
    require(msg.sender == _contractor );

    // require the money sent to escrow to be at least the payment of the job plus the comission.
    require(escrowfunds == _payAMount + _comish);

    // ensure variable seet to false
    bool escrowFullyFunded = false;

    // assign trade parties
    address employer = _contractor; //the person offering the Job
    address worker = _worker; //the person accepting the job

    // set state to initiated
    initiated = true;

    // event emission
    //emit Initiated(msg.sender, buyer, seller, _purchaseAmount);
    //emit Terms(purchaseAmount, totalEscrowAmount, buyerRequiredEscrow, sellerRequiredEscrow);
  }




}
